import React, { useState, useEffect } from 'react';
import axios from 'axios';
import  'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const [data, getData] = useState( [] );
  /*const [comments, getComment] = useState( [] );*/
  
  useEffect(() => {
    axios
    .get('https://jsonplaceholder.typicode.com/posts')
    .then(result => getData(result.data))

  }, [])
  /*useEffect(() => {
    axios
    .get('https://jsonplaceholder.typicode.com/posts/1/comments')
    .then(result => getComment(result.comments))

  }, [])*/

  function onClickComments() {
    
    const [comments, getComment] = useState( [] );

    useEffect(() => {
      axios
      .get('https://jsonplaceholder.typicode.com/posts/1/comments')
      .then(result => getComment(result.comments))
    }, [])
  
    console.log(comments);
    
  }
  
  return (
    <div class="container">
      
      {
        data.map(post => (
          <div>
            <div class="card">
              <h1 key={post.id} class="card-title"> {post.id}. {post.title} </h1>
              <h5 key={post.id} class="card-subtitle"> {post.body} </h5> 
              <button href="#" class="card-link" onClick= "console.log('Le lien a été cliqué.')">Comment</button>
            </div>
            <p>


            </p>
          </div>
        ))
      }
        
    </div>
  );
}

export default App;
