import React, { useState, useEffect } from 'react';
import axios from 'axios';
import  'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const [data, getData] = useState( [] );
  
  useEffect(() => {
    axios
    .get('https://jsonplaceholder.typicode.com/posts')
    .then(result => getData(result.data))

  }, [])
  
  return (
    <div class="container">
      
      {
        data.map(posts => (
          <div>
            <div class="card">
              <h1 key={posts.id} class="card-title"> {posts.id}. {posts.title} </h1>
              <h5 key={posts.id} class="card-subtitle"> {posts.body} </h5> 
              <button href="#" class="card-link" onClick= "console.log('Le lien a été cliqué.')">Comment</button>
            </div>
            <p>


            </p>
          </div>
        ))
      }
        
    </div>
  );
}

export default App;
