import React, { useState, useEffect } from 'react';
import axios from 'axios';
import  'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const [posts, setPosts] = useState( [] );
  const [comments, setComments] = useState( [] );

  

  useEffect(() => {
    axios
    .get('https://jsonplaceholder.typicode.com/posts')
    .then(result => setPosts(result.posts))

  }, [])
  useEffect(() => {
    axios
    .get('https://jsonplaceholder.typicode.com/posts/comments')
    .then(result => setComments(result.comments))

  }, [])

  return (
    <div class="container">
      
      {
        posts.map(post => (
          <div>
            <div class="card">
              <h1 key={posts.id} class="card-title"> {post.id}. {post.title} </h1>
              <h5 key={posts.id} class="card-subtitle"> {post.body} </h5> 
              <button href="#" class="card-link" onClick= "console.log('Le lien a été cliqué.')">Comment</button>
              <h5 key={comments.id} class="car-subtitle"> {comments.body} </h5>
            </div>
            <p>


            </p>
          </div>
        ))
      }
    </div>
  );
}

export default App;
